class ErrorsController < ApplicationController
  def error_404
    @not_found_path = params[:not_found]
    image_data = ''
    if params[:not_found] =~ /uploads/
      send_data Base64.decode64(image_data), type: 'image/png', disposition: 'inline'
    else
      render nothing: true
    end
  end

  def error_500
  end
end